        c
    }

    render(){
        return(
            <div className="newCandidateMainDiv">
                <input className="newCandInputs" placeholder='Candidate Name' id="candidate_name" onChange={this.handleChange}></input>
                <input className="newCandInputs" placeholder='Candidate Party' id="candidate_details" onChange={this.handleChange}></input>
                <button className='newCandAddBtn' onClick={this.addCandidates}>Add</button>
            </div>
        )
    }
}

export default NewCandidate;
