import React, { Component } from 'react';
import "../css/admin.css"
class Admin extends React.Component{
    state={
        isAuth:false,
        pass:""
    }
    isAuthenticated=async ()=>{
        console.log("hello from isAuthenticated");
        const res=await fetch("/isauth",{
            method:"GET",
            credentials:"include",
            headers:{
                "Content-Type":"applicaiton/json"
            }
        })
    }
    // componentDidMount=async()=>{
    //     const res=await fetch("/isauth",{
    //         method:"GET",
    //         credentials:"include",
    //         headers:{
    //             "Content-Type":"applicaiton/json"
    //         }
    //     }).then((res)=>{
    //         console.log("here")
    //         if(res.status===200){
    //             this.setState({isAuth:true});
    //         }
    //     })
    // }
    loginAPI=async ()=>{
        const response=await fetch("/login",
        {
            method:"POST",
            credentials:"include",
            headers:{
                "Content-Type":"application/json",
            },
            body:JSON.stringify({
                password:this.state.pass,
            })
        }).then((res)=>{
            if(res.status===200){
                this.setState({isAuth:true});
            }
        })
    }
    render(){
        return(
            <div className="mainDivAdmin">
                <div className="headingDivAdmin"><span className="adminTxtAdmin">Admin</span></div>
                {this.isAuthenticated().then((res)=>{if(res.status==200){return <>
                    <div className='loginPageDiv'>
                        <div className='loginPageButtons'><span>New Election</span> &gt;</div>
                        <div className='loginPageButtons'><span>Elections</span> &gt;</div>
                    </div>
                </>}
                else{
                    return<>
                        <div className="bottomDivAdmin">
                            <div className='passwordDivAdmin'>
                                <div className="passwordTxtDivAdmin">         
                                    <span className='passwordTxtAdmin'>Password</span>                 
                                </div>
                                <input className='inputForPassAdmin' type='password' 
                                onChange={(e)=>{this.setState({pass:e.target.value})}} ></input>
                                <button className='submitButtonAdmin' onClick={this.loginAPI}>Submit</button>
                            </div>
                        </div>
                    </>
                }})

                }
            </div>)
    }
}
export default Admin;
