import React, { Component } from 'react';
import {Link} from "react-router-dom"
import AdminImage from "../media/admin.png"
import PeopleImage from "../media/people.png"
import "../css/home.css"
import Election from "../build/Election.json"
class Home extends React.Component{

    async componentWillMount() {
        await this.loadWeb3()
        await this.loadBlockchainData()
    }
    
    async loadWeb3() {
        if (window.ethereum) {
            window.web3 = new Web3(window.ethereum)
            await window.ethereum.enable()
        }
        else if (window.web3) {
            window.web3 = new Web3(window.web3.currentProvider)
        }
        else {
            window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
        }
    }

    async loadBlockchainData() {
        const web3 = window.web3
        const accounts = await web3.eth.getAccounts()
        console.log(accounts)
        this.setState({ account: accounts[0] })
        const networkId = await web3.eth.net.getId()
        const networkData = Election.networks[networkId]
        if(networkData) {
            const election = new web3.eth.Contract(Election.abi, networkData.address)
            this.setState({ election })
            // const candCount = await election.methods.candidatesCount().call()
            // this.setState({ candCount })
            // for (var i = 1; i <= candCount; i++) {
            //     const candidates = await election.methods.candidates(i).call()
            //     if(candidates.election_id === this.state.id){
            //         this.setState({
            //             candidates: [...this.state.candidates, candidates]
            //         })
            //     }
            // }
            // console.log(this.state.candidates)
        } else {
            window.alert('Election contract not deployed to detected network.')
        }
    }
    render(){
        return(
            <div className="mainHomeDiv">
                <Link to="/user" className='userLinkAdmin'>
                    <div className="userHomeDiv">
                        <img className="adminImage" src={PeopleImage} alt="peopleImg"/>
                        <span className='userTxt'>User</span>
                    </div>
                </Link>
                <Link to="/admin" className='adminLinkAdmin'>
                    <div className="adminHomeDiv">
                        <img className="adminImage" src={AdminImage} alt="adminImg"/>
                        <span className='adminTxt'>Admin</span>
                    </div>
                </Link>
            </div>)
    }
}

export default Home;
