import './App.css'; 
import React, { Component } from 'react';
import {BrowserRouter,Routes,Route} from "react-router-dom"
import MyApp from "./Containers/MyApp"
import Home  from "./Containers/Home"
import Admin  from "./Containers/Admin"
import User  from "./Containers/User"
import NewCandidate from "./Containers/NewCandidate"
import Election from "./build/Election.json"
class App extends React.Component {
  async componentWillMount() {
    await this.loadWeb3()
    await this.loadBlockchainData()
}

async loadWeb3() {
    if (window.ethereum) {
        window.web3 = new Web3(window.ethereum)
        await window.ethereum.enable()
    }
    else if (window.web3) {
        window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
        window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
    }
}

async loadBlockchainData() {
    const web3 = window.web3
    const accounts = await web3.eth.getAccounts()
    console.log(accounts)
    this.setState({ account: accounts[0] })
    const networkId = await web3.eth.net.getId()
    const networkData = Election.networks[networkId]
    if(networkData) {
        const election = new web3.eth.Contract(Election.abi, networkData.address)
        this.setState({ election })
        console.log(election)
        const candCount = await election.methods.candidatesCount().call()
        this.setState({ candCount })
        console.log(candCount)
        for (var i = 1; i <= candCount; i++) {
            const candidates = await election.methods.candidates(i).call()
            if(candidates.election_id === this.state.id){
                this.setState({
                    candidates: [...this.state.candidates, candidates]
                })
            }
        }
        console.log(this.state.candidates)
    } else {
        window.alert('Election contract not deployed to detected network.')
    }
}
  render(){
    return (
      <BrowserRouter>
        <Routes>
            <Route exact path="/" element={<Home/>}/>
            <Route exact path="/user" element={<User/>}/>
            <Route exact path="/admin" element={<Admin/>}/>
            <Route exact path="/admin/addcandidate" element={<NewCandidate/>}/>
        </Routes>
      </BrowserRouter>
    );
  }
}
export default App;
